import React, { useState, useRef } from "react";
import axios from "axios";
import { Container, Typography, TextField, Button, Card, CardContent, CardMedia, Grid, CircularProgress } from "@mui/material";
import Webcam from "react-webcam";
import "./styles.css";

const App = () => {
  const [text, setText] = useState("");
  const [emotion, setEmotion] = useState("");
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [webcamActive, setWebcamActive] = useState(false);
  const webcamRef = useRef(null);

  const analyzeText = async () => {
    setLoading(true);
    setEmotion("");
    setSongs([]);
    try {
      const res = await axios.post("http://localhost:5000/text-emotion", { text });
      setEmotion(res.data.emotion);
      setSongs(res.data.songs || []);
    } catch (error) {
      setEmotion("neutral");
      setSongs([]);
    }
    setLoading(false);
  };

  return (
    <Container maxWidth={false} className="app-container">
      {/* HEADER */}
      <header className="header">
        <img src="/spotify_logo.png" alt="Spotify Logo" className="logo" />
        <Typography variant="h4" className="title">Spotify Emoter</Typography>
      </header>

      {/* MAIN CONTENT */}
      <section className="content">
        {/* Input Section */}
        <div className="input-section">
          <TextField
            label="How are you feeling?"
            variant="outlined"
            fullWidth
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="input-field"
          />
          <Button variant="contained" className="analyze-btn" onClick={analyzeText} disabled={loading || !text}>
            {loading ? <CircularProgress size={24} /> : "Analyze Text"}
          </Button>
        </div>

        {/* Webcam Section */}
        <div className="webcam-section">
          <Button variant="contained" className="webcam-btn" onClick={() => setWebcamActive(!webcamActive)} disabled={loading}>
            {webcamActive ? "Analyzing... (5s)" : "Start Webcam"}
          </Button>
          {webcamActive && <Webcam ref={webcamRef} className="webcam" />}
        </div>
      </section>

      {/* Emotion Display */}
      {emotion && !loading && (
        <Typography variant="h5" align="center" className="emotion-text">
          Detected Emotion: <span className="emotion">{emotion}</span>
        </Typography>
      )}

      {/* SONGS DISPLAY */}
      {songs.length > 0 && !loading && (
        <Grid container spacing={2} className="songs-grid">
          {songs.map((song, index) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
              <Card className="song-card">
                {song.album_image && <CardMedia component="img" image={song.album_image} className="song-image" />}
                <CardContent>
                  <Typography variant="subtitle1">
                    <a href={song.url} target="_blank" rel="noopener noreferrer" className="song-link">
                      {song.name}
                    </a>
                  </Typography>
                  <Typography variant="body2" color="text.secondary">{song.artist}</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default App;
