import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import {
  Container,
  Typography,
  TextField,
  Button,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  CircularProgress,
} from "@mui/material";
import Webcam from "react-webcam";
import "./styles.css";

const App = () => {
  const [text, setText] = useState("");
  const [emotion, setEmotion] = useState("");
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [webcamActive, setWebcamActive] = useState(false);
  const webcamRef = useRef(null);

  const analyzeText = async () => {
    setLoading(true);
    setEmotion("");
    setSongs([]);
    try {
      const res = await axios.post("http://localhost:5000/text-emotion", { text });
      setEmotion(res.data.emotion);
      setSongs(res.data.songs);
    } catch (error) {
      console.error("Text analysis error:", error);
      setEmotion("neutral");
      setSongs([]);
    }
    setLoading(false);
  };

  const analyzeFace = async () => {
    setLoading(true);
    setEmotion("");
    setSongs([]);
    const imageSrc = webcamRef.current.getScreenshot();
    try {
      const res = await axios.post("http://localhost:5000/face-emotion", { image: imageSrc });
      setEmotion(res.data.emotion);
      setSongs(res.data.songs);
    } catch (error) {
      console.error("Face analysis error:", error);
      setEmotion("neutral");
      setSongs([]);
    }
    setLoading(false);
    setWebcamActive(false); // Stop webcam after one analysis
  };

  useEffect(() => {
    let timeout;
    if (webcamActive) {
      setLoading(true);
      timeout = setTimeout(() => {
        analyzeFace();
      }, 5000); // Wait 5 seconds before analyzing
    }
    return () => clearTimeout(timeout);
  }, [webcamActive]);

  return (
    <Container maxWidth="md" className="app-container">
      <motion.div
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <Typography variant="h2" align="center" gutterBottom className="title">
          ðŸŽµ Emotion Music Recommender
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary">
          Let your mood guide your music!
        </Typography>
      </motion.div>

      <div className="input-section">
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <TextField
            label="How are you feeling?"
            variant="outlined"
            fullWidth
            value={text}
            onChange={(e) => setText(e.target.value)}
            sx={{ mb: 2 }}
          />
          <Button
            variant="contained"
            color="primary"
            onClick={analyzeText}
            disabled={loading || !text}
            fullWidth
          >
            {loading ? <CircularProgress size={24} /> : "Analyze Text"}
          </Button>
        </motion.div>

        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="webcam-section"
        >
          <Button
            variant="contained"
            color="secondary"
            onClick={() => setWebcamActive(!webcamActive)}
            disabled={loading}
            fullWidth
            sx={{ mt: 2 }}
          >
            {webcamActive ? "Analyzing... (5s)" : "Start Webcam"}
          </Button>
          {webcamActive && (
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              width="100%"
              className="webcam"
            />
          )}
        </motion.div>
      </div>

      {loading && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <Typography variant="h6" align="center" sx={{ mt: 4 }}>
            Detecting your emotion...
          </Typography>
        </motion.div>
      )}

      {emotion && !loading && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <Typography variant="h5" align="center" sx={{ mt: 4 }}>
            Detected Emotion: <span className="emotion">{emotion}</span>
          </Typography>
        </motion.div>
      )}

      {songs.length > 0 && !loading && (
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Card sx={{ mt: 4 }}>
            <CardContent>
              <Typography variant="h6">Your Playlist</Typography>
              <List>
                {songs.map((song, index) => (
                  <motion.li
                    key={index}
                    initial={{ x: -100 }}
                    animate={{ x: 0 }}
                    transition={{ duration: 0.3, delay: index * 0.1 }}
                  >
                    <ListItem>
                      <ListItemText
                        primary={
                          <a href={song.url} target="_blank" rel="noopener noreferrer" className="song-link">
                            {song.name}
                          </a>
                        }
                        secondary={song.artist}
                      />
                    </ListItem>
                  </motion.li>
                ))}
              </List>
            </CardContent>
          </Card>
        </motion.div>
      )}
    </Container>
  );
};

export default App;
